import styled from 'styled-components';

export const Container = styled.div`
width: 100%;
margin-top: 18px;
display: flex;
justify-content: space-between;
  input{
    width: 100%;
    padding: 12px 20px;
    border: none;
    border: 1px solid #BDBDBD;
    border-radius: 12px;
    margin-right: 25px;
  }
  button{
    padding: 20px;
    border-radius: 12px;
    border: none;
    background: #2F80ED;
    box-shadow: 0px 2px 6px rgba(127, 177, 243, 0.4);
    color: white;
  }
`;
