import styled from 'styled-components';

export const NavigationBar = styled.nav`
  display: flex;
  width: 100%;
  justify-content: center;
  align-items: center;
  border-bottom: 1px solid #BDBDBD;

  button{
    border: none;
    background: transparent;
    padding: 0 45px;
    font-size: 14px;
  }
`;
