import { useState } from 'react';
import { Container, SearchBarContainer } from './styles';
import Header from '../header';
import NavBar from '../navBar';

export default function Home() {
  const [todoList] = useState([
    { id: Math.random(), tarefa: 'Do coding Challenges' },
    { id: Math.random(), tarefa: 'Do coding Challenges' },
    { id: Math.random(), tarefa: 'Do coding Challenges' },
    { id: Math.random(), tarefa: 'Do coding Challenges' },
  ]);
  const [todoName, setTodoName] = useState('');

  function handleChangeText(event) {
    console.log(event.target.value);
  }

  function handleInsertNewTodo() {
    console.log('Clicou');
  }

  return (
    <Container>
      <Header />
      <NavBar />
      <SearchBarContainer>
        <input type="text" placeholder="add details" onChange={handleChangeText} />
        <button type="button" onClick={handleInsertNewTodo}>Add</button>
      </SearchBarContainer>
      <div className="todoContainer">
        {todoList.map((todo) => (
          <label htmlFor={todo.id}>
            <input
              type="checkbox"
              id={todo.id}
              name={todo.id}
            />
            {todo.tarefa}
            <br />
          </label>

        ))}
      </div>

    </Container>

  );
}
